// Helper functions
function getMenuItemByName(name) {
    return MENU_DATA.find(item => item.name === name);
}

function getItemsByCategory(category) {
    return MENU_DATA.filter(item => item.category === category);
}

function getItemsByMenuType(menuType) {
    return MENU_DATA.filter(item => item.menu_type === menuType);
}

function searchItems(query) {
    const lowerQuery = query.toLowerCase();
    return MENU_DATA.filter(item => 
        item.name.toLowerCase().includes(lowerQuery) ||
        item.description.toLowerCase().includes(lowerQuery) ||
        item.category.toLowerCase().includes(lowerQuery)
    );
}
